eg-group.codeblock:
// 日付マスク書式 MM/DD/YYYY 用の JavaScript コード
var re = /^[mdy0-9]{2}\/[mdy0-9]{2}\/[mdy0-9]{4}$/
//Allow blank space in field
if (event.value !="") {
  if (re.test(event.value) == false) {
    app.alert ({
       cTitle: "誤った書式",
       cMsg: "mm/dd/yyyy の書式で日付を入力してください。"
    });
  }
}
